mapper.select:
SELECT (@rownum:=@rownum+1) AS serialNumber, cmt.comment_id AS commentId, cmt.product_id AS productId, cmt.user_id AS userId, cmt.content_x AS commentX, cmt.status_x AS statusX, cmt.crtBy_m AS crtByM, cmt.crtOn_dt AS crtOnDt, cmt.crtBy_m AS crtByM, DATE_FORMAT(cmt.crtOn_dt, '%Y-%m-%d %H:%i') AS createDate, u.username_m AS username, p.imagePath_x AS imagePath, p.name_x AS productName FROM (SELECT @rownum:=0) a, mps_comment cmt LEFT JOIN mps_product p ON cmt.product_id = p.product_id LEFT JOIN mps_user u ON cmt.user_id = u.user_id WHERE 1=1
AND p.category_id = #{categoryId}
AND p.categorySecond_id = #{categorySecondId}
