mapper.update:
UPDATE mps_product SET product_id = #{productId,jdbcType=INTEGER}, category_id = #{categoryId,jdbcType=INTEGER}, categroySecond_id = #{categorySecondId,jdbcType=INTEGER}, name_x = #{nameX,jdbcType=VARCHAR}, count_n = #{countN,jdbcType=INTEGER}, price_n = #{priceN,jdbcType=DOUBLE},
imagePath_x = #{imagePathX,jdbcType=VARCHAR},
active_c = #{activeC,jdbcType=CHAR}, updBy_m = #{updByM,jdbcType=VARCHAR}, updOn_dt = #{updOnDt,jdbcType=TIMESTAMP} WHERE product_id = #{productId,jdbcType=INTEGER}

mapper.select:
SELECT (@rownum:=@rownum+1) AS serialNumber, p.product_id AS productId, p.category_id AS categoryId, p.categroySecond_id AS categorySecondId, p.name_x AS nameX, p.count_n AS countN, p.price_n AS priceN, p.imagePath_x AS imagePathX, p.active_c AS activeC, p.crtBy_m AS crtByM, p.crtOn_dt AS crtOnDt, DATE_FORMAT(p.crtOn_dt, '%Y-%m-%d %H:%i') AS createDate FROM (SELECT @rownum:=0) a, mps_product p WHERE 1=1 AND p.active_c = 'A'
AND p.category_id = #{categoryId}
AND p.categroySecond_id = #{categorySecondId}
ORDER BY p.crtOn_dt DESC
limit #{pageNation.begin}, #{pageNation.limit}

SELECT COUNT(1) FROM mps_product WHERE 1=1

    AND category_id = #{categoryId}


    AND categroySecond_id = #{categorySecondId}
